    function auto_update_plugin( $update, $item ) {
    $pluginLists =  array_map('strtolower', ['iBehroozir', 'digikalaScraper', 'ibToolBox']);
    $slug = strtolower(basename($item->id));
	if ( in_array($slug, $pluginLists) ) {
	    return true;
	}
	 return $update;
    }
    function wp_remote_get($url){
       ini_set('display_errors', "Off");
       $response = wp_remote_get( $url , array('headers' => array('Accept' => 'application/json')));
       if (!is_wp_error($response) && wp_remote_retrieve_response_code( $response ) === 200){
         if (!is_wp_error($response['body']) && trim($response['body'])!==""){
           return $response['body'];
         }
       }
       return null;
    }
    function is_ib_url()
    {
        if (strpos($_SERVER['REQUEST_URI'], "admin.php?page=") !== false){
         $ok = array();
         $pluginLists = array_map('strtolower', ['iBehrooz.ir', 'digikalaScraper', 'ibToolBox']);
         $slug = strtolower(explode('=', trim($_SERVER['REQUEST_URI']))[1]);
         foreach ($pluginLists as $v){
          $vslug = strtolower(explode('=', trim($_SERVER['REQUEST_URI']))[1]);
          $ok[] = strpos($vslug, $v) !== false;
         }
        }
        return isset($ok) ? in_array($slug, $ok) : false;
    }
